$(function () {
    $('#m-customers').addClass('active');
    
    // btn create customers 

        $('body').on('click', '.createContracts', function (e) {
            e.preventDefault();
            var _this = $(this);
            $(".form-contracts")[0].reset();
            $("#form-contracts-id").val('');
        });

    //  form update Contracts

    $('body').on('submit', '.form-contracts', function (e) {
        e.preventDefault();
        var form = new FormData(this);
        $.ajax({
            url: SITEURL + "/customers/updateContracts",
            dataType: 'json',
            type: 'POST',
            data: form,
            processData: false,
            contentType: false,
            beforeSend: function () {
                $(".preload").fadeIn(300);
            },
            success: function (result) {
                $(".preload").fadeOut(300);
                toastr[result.status](result.msg);
                if (result.status == "success") {
                    $('.clear').val('');
                    $('.modal-close').click();
                    setTimeout(function(){
                        location.reload();
                    },800);
                }
            }
        });
    });
    
            
    // update Customers
    
    $('body').on('click', '.editContracts', function (e) {
        e.preventDefault();
        var _this = $(this);
        var element = "#"+_this.attr("data-element");
        $.ajax({
            url: SITEURL + "/customers/getContracts",
            dataType: 'json',
            type: 'POST',
            data: {id : _this.attr("data-id")},
            success: function (result) {
                if (result.status == "success") {                   
                    $(element+'from').val(result.model.from);
                    $(element+'to').val(result.model.to); 
                    $(element+'comments').val(result.model.comments);
                    $(element+'id').val(result.model.id);                    
                }else{
                    toastr[result.status](result.msg);
                    
                }
            }
        });

    });
    
});


