$(function () {
    
    
    $('body').on('change', '.select-cluster', function (e) {
        e.preventDefault();
        var _this = $(this);        
        if(_this.val() != ''){
            location.href = SITEURL+"/cluster/"+_this.val(); 
        }        
    });
    
});

$(window).on('load', function() {
    setTimeout(function(){
        $('#m-wallets').trigger('click');
        $('#m-dashboard').addClass('active');
    },250);
});