$(function () {
        
    Highcharts.setOptions({
        global: {
            useUTC: false
        },
        lang: {
            thousandsSep: ','
        }
     });
     
    var seriesData = [];
    var seriesCategory = [];
    var baseColor = '';

    /**
     * Create the chart when all data is loaded
     * @returns {undefined}
     */
    function createChart() {

        var formElement = document.querySelector(".form-filter-indicators");
        var form = new FormData(formElement);

        $.ajax({
            url: SITEURL + "/indicators/getChartPlacement",
            async: false,
            dataType: 'json',
            type: 'POST',
            data: form,
            processData: false,
            contentType: false,
            success: function (result) {
                
                //if (result.status == "success") {                    
                seriesData = result.data;
                seriesCategory = result.category;
                //baseColor = result.color;
                //}
            }
        });
        
        Highcharts.chart('canvas', {
            chart: {
                type: 'line'
            },
            title: {
                text: 'COLOCACIÃ“N'
            },
            xAxis: {
                categories: seriesCategory
            },
            yAxis: {
                title: {
                    text: 'Valor'
                },
//                min: 0,
//                max: 50000000000000,
//                startOnTick: false,
//                endOnTick: false
            },
            plotOptions: {
                line: {
                    dataLabels: {
                        //enabled: true
                    },
                    //enableMouseTracking: false
                }
            },
            legend: {
                enabled: false,
            },
            series: seriesData
        });
        

        setTimeout(function(){
            $('.highcharts-credits').fadeOut('slow');
        },500);
    }

    createChart();
    
    $('body').on('change', '.form-indicators', function (e) {
        e.preventDefault();
        createChart();
    });  
      
});
$(window).on('load', function() {
    setTimeout(function(){
        $('#m-indicators').addClass('active');
    },300);
});


