$(function () {
        
    Highcharts.setOptions({
        global: {
            useUTC: false
        },
        lang: {
            thousandsSep: ','
        }
     });
     
    var seriesData = [];
    var seriesCategory = [];
    var baseColor = '';

    /**
     * Create the chart when all data is loaded
     * @returns {undefined}
     */
    function createChart5() {

        var formElement = document.querySelector(".form-filter-indicators");
        var form = new FormData(formElement);

        $.ajax({
            url: SITEURL + "/indicators/getChartPlacement5",
            async: false,
            dataType: 'json',
            type: 'POST',
            data: form,
            processData: false,
            contentType: false,
            success: function (result) {
                
                //if (result.status == "success") {                    
                seriesData = result.data;
                seriesCategory = result.category;
                //baseColor = result.color;
                //}
            }
        });
        
        Highcharts.chart('canvas5', {
            chart: {
//                polar: true,
                type: 'line'
            },

            title: {
                text: 'MODELOS ML',
            },

            pane: {
                size: '80%'
            },

            xAxis: {
                categories: ['ENE 2019', 'FEB 2019', 'MAR 2019',],
                tickmarkPlacement: 'on',
                lineWidth: 0
            },

            yAxis: {
                gridLineInterpolation: 'polygon',
                lineWidth: 0,
                min: 0
            },

            tooltip: {
                shared: true,
                pointFormat: '<span style="color:{series.color}">{series.name}: <b>${point.y:,.0f}</b><br/>'
            },

            

            series: [{
                name: 'CLUSTER A',
                 data: [18301432643 ,  22504462483,  28501462683,],
                pointPlacement: 'on'
            },{
                name: 'CLUSTER B',
                 data: [ 5934536945, 8414636984 ,  10914536984,],
                pointPlacement: 'on'
            },{
                name: 'CLUSTER C',
                 data: [ 10174596415,  17400596415,  20174596415,],
                pointPlacement: 'on'
            },],

            responsive: {
                rules: [{
                    condition: {
                        maxWidth: 700
                    },
                    chartOptions: {
                        legend: {
                            align: 'center',
                            verticalAlign: 'bottom'
                        },
                        pane: {
                            size: '80%'
                        }
                    }
                }]
            }
            
        });
        

        setTimeout(function(){
            $('.highcharts-credits').fadeOut('slow');
        },500);
    }

    createChart5();
    
    $('body').on('change', '.form-indicators', function (e) {
        e.preventDefault();
        createChart5();
    });  
      
});



