$(function () {
        
    var urlExplorar = 'billing/payments';
    
    $('body').on('change', '.searchPayments', function () {
        var i = 0;
        $('.searchPayments').each(function () {
            var _this = $(this);

            if (_this.val() != '') {

                urlExplorar += (i == 0)? '?' + _this.attr('name') + '=' + _this.val() : '&' + _this.attr('name') + '=' + _this.val();
                i++;
            }

        });
        window.location = SITEURL + '/' + urlExplorar;
        return false;
    });          
});
$(window).on('load', function() {    
     setTimeout(function(){
        $('#m-wallets').trigger('click');
        $('#m-billing').addClass('active');
     },250);
});

