$(function () {
    
    var seriesOptions = [];
    var title = '';
    var bg = [];
        
    function createChart() {

        var formElement = document.querySelector(".form-filter-indicators");
        var form = new FormData(formElement);

        $.ajax({
            url: SITEURL + "/indicators/getChartPerformance",
            async: false,
            dataType: 'json',
            type: 'POST',
            data: form,
            processData: false,
            contentType: false,
            success: function (result) {                 
                seriesOptions = result.data;
                title = result.title;
                bg = result.bg;
            }
        });

        Highcharts.chart('canvas-5', {

            chart: {
                type: 'solidgauge',
                height: '40%',
                marginTop: 50,
                events: {
                }
            },

            title: {
                text: title,
                style: {
                    fontSize: '24px'
                }
            },
            legend: {
                enabled: true,
                itemStyle: {
                    "fontWeight": 'normal',
                    "fontSize": "10px",
                }
            },
            tooltip: {
                useHTML: true,
                borderWidth: 0,
                backgroundColor: 'none',
                shadow: false,
                style: {
                    fontSize: '14px'
                },
                pointFormat: '<div style="text-align: center; font-weight: bold;"> {series.name}<br><span style="font-size:2em; color: {point.color}; font-weight: bold;">{point.y}%</span> </div>',
                positioner: function (labelWidth) {
                    var xp = (labelWidth < 150)? 27 : (-10); 

                    return {
                        x: (((this.chart.chartWidth - labelWidth) / 4) * 3.2),
                        y: (this.chart.plotHeight / 2) + 18
                    };
                }
            },
            pane: {
                startAngle: 0,
                endAngle: 360,
                background: bg 
                        
            },
            yAxis: {
                min: 0,
                max: 100,
                lineWidth: 0,
                tickPositions: []
            },
            plotOptions: {
                solidgauge: {
                    dataLabels: {
                        enabled: false
                    },
                    linecap: 'round',
                    stickyTracking: false,
                    rounded: true
                }
            },
            legend: {      
                symbolPadding: 0,
                symbolWidth: 0.1,
                symbolHeight: 0.1,
                symbolRadius: 0,
                useHTML: true,
                symbolWidth: 0,
                labelFormatter: function() {  
                  return '<div style="height:22px; width: 22px; display: inline-block; background-color: '+ this.data[0].color + '"></div><div style="color: #000000; display: inline-block; position: relative; top: -7px; margin: 0 15px;">'+ this.name + ' : '+ this.data[0].z + '</div>';        
                },
            },
            series: seriesOptions,
            exporting: {
                enabled: false
            }            
        });
        setTimeout(function(){
            $('.highcharts-credits').fadeOut('slow');
        },500);
    }

    createChart();
        
    $('body').on('change', '.form-indicators', function (e) {
        e.preventDefault();
        createChart();
    });  
      
});
$(window).on('load', function() {    
     setTimeout(function(){
        $('#m-wallets').trigger('click');
        $('#m-performance').addClass('active');
     },250);
});