$(function () {
    
    setTimeout(function(){
        $('#m-settings').trigger('click');
    },650);
    
    $('body').on('submit', '.form-political', function (e) {
        e.preventDefault();
        var form = new FormData(this);
        $.ajax({
            url: SITEURL + "/settings/savePolitical",
            dataType: 'json',
            type: 'POST',
            data: form,
            processData: false,
            contentType: false,
            beforeSend: function () {
                $(".preload").fadeIn(300);
            },
            success: function (result) {
                $(".preload").fadeOut(300);
                toastr[result.status](result.msg);
                if (result.status == "success") {
                      $('#load_db').closeModal();
                    setTimeout(function () {
                        location.reload();
                    }, 200);
                }
            }
        });
    });
    
});


