$(function () {

    //  form profile update User

    $('body').on('submit', '.form_user', function (e) {
        e.preventDefault();
        var form = new FormData(this);
        $.ajax({
            url: SITEURL + "/dashboard/updateUser",
            dataType: 'json',
            type: 'POST',
            data: form,
            processData: false,
            contentType: false,
            beforeSend: function () {
                $(".preload").fadeIn(300);
            },
            success: function (result) {
                $(".preload").fadeOut(300);
                toastr[result.status](result.msg);
                if (result.status == "success") {
                    $('.clear').val('');
                    $('.user-name').html(result.model.name);
                    $('.img-user').attr('src', result.model.image);
                }
            }
        });
    });
    
    // form profile customers update
    
    $('body').on('submit', '.form-profile-customers', function (e) {
        e.preventDefault();
        var form = new FormData(this);
        $.ajax({
            url: SITEURL + "/dashboard/updateCustomer",
            dataType: 'json',
            type: 'POST',
            data: form,
            processData: false,
            contentType: false,
            beforeSend: function () {
                $(".preload").fadeIn(300);
            },
            success: function (result) {
                $(".preload").fadeOut(300);
                toastr[result.status](result.msg);
                if (result.status == "success") {
                    $('.clear').val('');
                    $('.user-name').html(result.model.name);
                    $('.img-user').attr('src', result.model.image);
                }
            }
        });
    });

});


