$(function () {
            
    var urlExplorar = 'properties/others';

    $('body').on('change', '.searchOthers', function () {
        var i = 0;
        $('.searchOthers').each(function () {
            var _this = $(this);

            if (_this.val() != '') {

                urlExplorar += (i == 0)? '?' + _this.attr('name') + '=' + _this.val() : '&' + _this.attr('name') + '=' + _this.val();
                i++;
            }

        });
        window.location = SITEURL + '/' + urlExplorar;
        return false;
    });
    
       
    
});

$(window).on('load', function () {
    setTimeout(function () {
        $('#m-wallets').trigger('click');
        $('#m-properties').addClass('active');
    }, 250);
});
