$(function () {
    //  form login

    $('body').on('submit', '.form_login', function (e) {
        e.preventDefault();
        var form = new FormData(this);
        $.ajax({
            url: SITEURL + "/services/login",
            dataType: 'json',
            type: 'POST',
            data: form,
            processData: false,
            contentType: false,
            beforeSend: function () {
                $(".preload").fadeIn(300);
            },
            success: function (result) {
                if (result.status == "success") {
                    setTimeout(function(){
                        location.href = result.url;                        
                    },800);
                } else {
                    $(".preload").fadeOut(300);
                    toastr[result.status](result.msg);
                    grecaptcha.reset();
                }
            }
        });
    });
    
});


