$( function() {
  
  $(".connectedSortable").sortable({
    connectWith: ".connectedSortable",
 //    start: function() {
	// },
	// drag: function() {
	// },
        receive: function(event, ui) {
            var _this = ui;
            $.ajax({
                url: SITEURL + '/business/stateUserBusiness',
                dataType: 'json',
                type: 'POST',
                data: {id : ui.item.attr("data-id"), idUserState : this.id},
                success: function (result) {
                    toastr[result.status](result.msg);
                    
                    if(result.status == 'error'){
                        _this.sender.sortable("cancel");   
                    }
                }
            });
            
    }
  }).disableSelection();
    
});