$(function () {    
    // btn create customers 

        $('body').on('click', '.createSuggestions', function (e) {
            e.preventDefault();
            var _this = $(this);
            $(".form-suggestions")[0].reset();
        });

    //  form update User

    $('body').on('submit', '.form-suggestions', function (e) {
        e.preventDefault();
        var form = new FormData(this);
        $.ajax({
            url: SITEURL + "/suggestions/updateSuggestions",
            dataType: 'json',
            type: 'POST',
            data: form,
            processData: false,
            contentType: false,
            beforeSend: function () {
                $(".preload").fadeIn(300);
            },
            success: function (result) {
                $(".preload").fadeOut(300);
                toastr[result.status](result.msg);
                if (result.status == "success") {
                    $('.clear').val('');
                    $('.modal-close').click();
                    setTimeout(function(){
                        location.reload();
                    },800);
                }
            }
        });
    });
        
    // update Customers
    
    $('body').on('click', '.editSuggestion', function (e) {
        e.preventDefault();
        var _this = $(this);
        var element = "#"+_this.attr("data-element");
        $.ajax({
            url: SITEURL + "/suggestion/getSuggestion",
            dataType: 'json',
            type: 'POST',
            data: {id : _this.attr("data-id")},
            success: function (result) {
                if (result.status == "success"){                    
                    $.each(result.model,function(index, value){
                        $(element+index).val(value);
                    });                    
                }else{
                    toastr[result.status](result.msg);                    
                }
            }
        });
    });    
});
$(window).on('load', function() {
    setTimeout(function () {
        $('#m-suggestions').addClass('active');
    }, 250);    
});

