$(function () {
      

var seriesOptions = [],
    seriesCounter = 0,
    names = ['ANDINA', 'AMAZONICA', 'CARIBE', 'ORINOQUIA', 'PACIFICA'];
    
    Highcharts.setOptions({
        lang: {
            thousandsSep: ',',
            months: [
                'Enero', 'Febrero', 'Marzo', 'Abril',
                'Mayo', 'Junio', 'Julio', 'Agosto',
                'Septiembre', 'Octubre', 'Noviembre', 'Diciembre'
            ],
            weekdays: [
                'Domingo', 'Lunes', 'Martes', 'Miercoles',
                'Jueves', 'Viernes', 'Sabado'
            ],
            rangeSelectorFrom: 'Desde',
            rangeSelectorTo: 'Hasta'
        },
    });


/**
 * Create the chart when all data is loaded
 * @returns {undefined}
 */
function createChart() {

    Highcharts.stockChart('canvas-3', {
        title: {
            text: 'DISTRIBUCIÓN GEOGRÁFICA',
            style: {
                "fontSize": "12px",
                "fontWeight": 'normal'
            },   
        },
        legend: {
            enabled: true,
            itemStyle: {
                "fontWeight": 'normal',
            }
            
        },
        rangeSelector: {
            verticalAlign: 'bottom',
            x: 0,
            y: 0,
            allButtonsEnabled: true,
            buttons: [{
                type: 'week',
                count: 1,
                text: '1 S'
            },{
                type: 'month',
                count: 1,
                text: '1 M'
            }, {
                type: 'month',
                count: 3,
                text: '3 M'
            }, {
                type: 'month',
                count: 6,
                text: '6 M'
            },{
                type: 'all',
                text: 'Todo'
            }],
            buttonTheme: {
                width: 30
            },
            selected: 2
        },
        plotOptions: {
            series: {                
                marker: {
                    enabled: true,
                    radious: 8,
                    symbol: 'circle'
                }
            },
        },
        tooltip: {
            useHTML: true,
            headerFormat: '<small>{point.key}aa</small><br><table>',
            pointFormat: '<tr><td style="color: {series.color}">{series.name}: </td></tr>' +
                '<tr><td style="text-align: right"><b>{point.y} EUR--</b></td></tr>'+
                '<tr><td style="text-align: right"><b>{point.y} EUR--</b></td></tr>',
            footerFormat: '</table>',
        },

        series: seriesOptions
    });
}

$.each(names, function (i, name) {

    //$.getJSON('https://www.highcharts.com/samples/data/' + name.toLowerCase() + '-c.json',    function (data) {
    
    var data = [
    {
        x: 1468818000,
        y: (98.7 + i),
        z: (100.13 + i),
    },
    {
        x: 1468935000000,
        y: (99.56 + i),
        z: (100 + i),
    },
    {
        x: 1469021400000,
        y: (100 + i),
        z: (100.46 + i),
    },
    {
        x: 1469107800000,
        y: (98.7 + i),
        z: (100.13 + i),
    },
    {
        x: 1469194200000,
        y: (99.56 + i),
        z: (100 + i),
    },
    {
        x: 1469453400000,
        y: (100 + i),
        z: (100.46 + i),
    },
    ];
    
        seriesOptions[i] = {
            name: name,
            data: data
        };

        // As we're loading the data asynchronously, we don't know what order it will arrive. So
        // we keep a counter and create the chart when all the data is loaded.
        seriesCounter += 1;

        if (seriesCounter === names.length) {
            createChart();
        }
    //});
});

setTimeout(function(){
        $('.highcharts-credits').fadeOut('slow');
    },500);

      
});


