    /**
     * Create the chart when all data is loaded
     * @returns {undefined}
     */
    function createChart() {

        var formElement = document.querySelector(".form-filter-indicators");
        var form = new FormData(formElement);

        $.ajax({
            url: SITEURL + "/indicators/getChartTendencies",
            async: false,
            dataType: 'json',
            type: 'POST',
            data: form,
            processData: false,
            contentType: false,
            success: function (result) {
                //if (result.status == "success") {                    
                seriesOptions = result.data;
                title = result.title;
                //}
            }
        });

        Highcharts.stockChart('canvas-5', {
            title: {
                text: title,
                style: {
                    "fontSize": "12px",
                    "fontWeight": 'bold'
                },   
            },
            legend: {
                enabled: true,
                itemStyle: {
                    "fontWeight": 'normal',
                    "fontSize": "10px",
                }

            },
            rangeSelector: {
                selected: 1,
                verticalAlign: 'bottom',
                x: 0,
                y: 0,
                allButtonsEnabled: true,
                buttons: [{
                    type: 'week',
                    count: 1,
                    text: '1 Sem'
                },{
                    type: 'month',
                    count: 1,
                    text: '1 Mes'
                }, {
                    type: 'month',
                    count: 3,
                    text: '3 Mes'
                }, {
                    type: 'month',
                    count: 6,
                    text: '6 Mes'                
                }, {
                    type: 'month',
                    count: 12,
                    text: '1 AÃ±o'
                },{
                    type: 'all',
                    text: 'Todo'
                }],
                buttonTheme: {
                    width: 35
                },                
            },
            plotOptions: {
                series: {                
                    marker: {
                        enabled: true,
                        radious: 8,
                        symbol: 'circle'
                    },
                },
            },
            tooltip: {
                useHTML: true,
                outside: true,
                formatter: function () {
                    var s = '<b class="txt_center">' + Highcharts.dateFormat('%A, %b %e, %Y', this.x) + '</b><br/>';
                    $.each(this.points, function () {
                        s += '<span style="color:' + this.color + '">\u25CF</span> ' + this.series.name + ': '+'<b> $ ' + Highcharts.numberFormat(this.y,0,'.',',') + '</b>  '+'<span><i class="feather feather-user"></i>  '+this.point.z+'</span><br/>';
                    });

                    return s;
                }
            },
            series: seriesOptions,
            exporting: {
                enabled: false
            } 
        });

        setTimeout(function(){
            $('.highcharts-credits').fadeOut('slow');
        },500);
    }

    createChart();
    
    $('body').on('change', '.form-indicators', function (e) {
        e.preventDefault();
        createChart();
    });  
      
