$(function () {
    setTimeout(function(){
        $('#m-wallets').trigger('click');
        $('#m-users').addClass('active');
    },650);
        
    // get sessions user - date
    
    $('body').on('click', '.view-sessions', function (e) {
        e.preventDefault();
        var _this = $(this);
        $.ajax({
            url: SITEURL + "/users/getSessions",
            dataType: 'json',
            type: 'POST',
            data: {id : _this.attr("data-user"), date : _this.attr("data-date")},
            success: function (result) {
                if (result.status == "success") {
                    $("#content-modal-sessions").html(result.html);
                    setTimeout(function(){
                        $("#modals-sessions").openModal();                         
                    },800);
                }else{
                    toastr[result.status](result.msg);                    
                }
            }
        });

    });
    
});


