$(function () {
    
    var urlExplorar = 'wallet';

    $('body').on('change', '.searchWallet', function () {
        var i = 0;
        $('.searchWallet').each(function () {
            var _this = $(this);

            if (_this.val() != '') {

                urlExplorar += (i == 0) ? ((_this.val() != '0') ? '/' + _this.val() : '') : (i == 1) ? '?' + _this.attr('name') + '=' + _this.val() : '&' + _this.attr('name') + '=' + _this.val();
                i++;
            }

        });
        window.location = SITEURL + '/' + urlExplorar;
        return false;
    });

});

